export const MenuSectionTitle = (props: ExpandProps<MenuSectionTitleProps>) => {
  const {
    children,
    testId = 'cf-ui-menu-section-title',
    className,
    ...otherProps
  } = props;

  const styles = getMenuSectionTitleStyles();

  return (
    <SectionHeading
      // Techincally, menus cannot contain headings according to ARIA.
      // We hide the heading from assistive technology, and only use it
      // as a label
      aria-hidden="true"
      testId={testId}
      className={cx(styles, className)}
      marginBottom="none"
      {...otherProps}
    >
      {children}
    </SectionHeading>
  );
};
import React, {
  useCallback,
  useEffect,
  useMemo,
  useRef,
  useState,
} from 'react';
import { Menu, MenuProps } from '../Menu';
import { useMenuContext } from '../MenuContext';
import { SubmenuContextProvider, SubmenuContextType } from '../SubmenuContext';
import { mergeRefs } from '@contentful/f36-core';

const SUBMENU_OFFSET: [number, number] = [-8, 2];

export type SubmenuProps = Omit<
  MenuProps,
  | 'placement'
  | 'offset'
  | 'usePortal'
  | 'isOpen'
  | 'isAutoalignmentEnabled'
  | 'defaultIsOpen'
>;

export const Submenu = (props: SubmenuProps) => {
  const { onClose, onOpen, ...otherProps } = props;

  const {
    isOpen: isParentMenuOpen,
    menuId,
    propsToPropagateToSubmenus,
  } = useMenuContext();

  const triggerRef = useRef<HTMLButtonElement>(null);
  const mouseLeaveTimerRef = useRef(null);

  const [isOpen, setIsOpen] = useState(false);
  const handleOpen = useCallback(() => {
    setIsOpen(true);
    window.clearTimeout(mouseLeaveTimerRef.current);

    onOpen?.();
  }, [onOpen]);
  const handleClose = useCallback(() => {
    setIsOpen(false);
    window.clearTimeout(mouseLeaveTimerRef.current);

    onClose?.();
  }, [onClose]);
  const closeAndFocusTrigger = useCallback(() => {
    handleClose();
    triggerRef.current?.focus({ preventScroll: true });
  }, [handleClose]);

  useEffect(() => {
    // close when parent menu closed
    if (isParentMenuOpen === false) {
      setIsOpen(false);
    }
  }, [isParentMenuOpen]);

  const contextValue: SubmenuContextType = useMemo(
    () => ({
      isOpen,
      getSubmenuListProps: (_props) => ({
        'data-parent-menu': menuId,
        onMouseOver: (event) => {
          handleOpen();

          _props.onMouseOver?.(event);
        },
        onMouseLeave: (event) => {
          closeAndFocusTrigger();

          _props.onMouseLeave?.(event);
        },
      }),
      getSubmenuTriggerProps: (_props, _ref) => ({
        ref: mergeRefs(triggerRef, _ref),
        onKeyDown: (event) => {
          if (event.key === 'ArrowRight') {
            event.preventDefault();
            handleOpen();
          }

          _props.onKeyDown?.(event);
        },
        onMouseOver: (event) => {
          handleOpen();

          _props.onMouseOver?.(event);
        },
        onMouseLeave: (event) => {
          mouseLeaveTimerRef.current = window.setTimeout(
            closeAndFocusTrigger,
            300,
          );

          _props.onMouseLeave?.(event);
        },
      }),
    }),
    [isOpen, menuId, handleOpen, closeAndFocusTrigger],
  );

  return (
    <SubmenuContextProvider value={contextValue}>
      <Menu
        {...propsToPropagateToSubmenus}
        {...otherProps}
        isOpen={isOpen}
        onClose={handleClose}
        onOpen={handleOpen}
        placement="right-start"
        offset={SUBMENU_OFFSET}
        isAutoalignmentEnabled={false}
      />
    </SubmenuContextProvider>
  );
};
import { css } from 'emotion';
import tokens from '@contentful/f36-tokens';

export const getSubmenuTriggerStyles = () => {
  return {
    root: ({ isActive }) =>
      css({
        display: 'flex',
        alignItems: 'center',
        paddingRight: tokens.spacingXs,
        ...(isActive
          ? {
              backgroundColor: tokens.gray100,
            }
          : {}),
      }),
    content: css({
      marginRight: tokens.spacingM,
    }),
    icon: css({
      marginLeft: 'auto',
      fill: 'currentColor',
    }),
  };
};
import React from 'react';
import { MenuTrigger } from '../MenuTrigger/MenuTrigger';
import { MenuItem, MenuItemProps } from '../MenuItem/MenuItem';
import { useSubmenuContext } from '../SubmenuContext';
import { ChevronRightIcon } from '@contentful/f36-icons';
import type { ExpandProps } from '@contentful/f36-core';
import { cx } from 'emotion';
import { getSubmenuTriggerStyles } from './SubmenuTrigger.styles';

export type SubmenuTriggerProps = Omit<
  MenuItemProps<'button'>,
  'isInitiallyFocused' | 'as'
>;

const _SubmenuTrigger = (
  props: ExpandProps<SubmenuTriggerProps>,
  ref: React.Ref<HTMLButtonElement>,
) => {
  const { className, children } = props;
  const { getSubmenuTriggerProps, isOpen } = useSubmenuContext();

  const styles = getSubmenuTriggerStyles();

  return (
    <MenuTrigger>
      <MenuItem
        {...props}
        {...getSubmenuTriggerProps(props, ref)}
        className={cx(styles.root({ isActive: isOpen }), className)}
      >
        <span className={styles.content}>{children}</span>
        <ChevronRightIcon className={styles.icon} />
      </MenuItem>
    </MenuTrigger>
  );
};

export const SubmenuTrigger = React.forwardRef(_SubmenuTrigger);
import { Menu as OriginalMenu } from './Menu';
import { MenuList } from './MenuList/MenuList';
import { MenuListHeader } from './MenuList/MenuListHeader';
import { MenuListFooter } from './MenuList/MenuListFooter';
import { MenuItem } from './MenuItem/MenuItem';
import { MenuTrigger } from './MenuTrigger/MenuTrigger';
import { MenuDivider } from './MenuDivider/MenuDivider';
import { MenuSectionTitle } from './MenuSectionTitle/MenuSectionTitle';
import { Submenu } from './Submenu/Submenu';
import { SubmenuTrigger } from './SubmenuTrigger/SubmenuTrigger';

type CompoundMenu = typeof OriginalMenu & {
  List: typeof MenuList;
  ListHeader: typeof MenuListHeader;
  ListFooter: typeof MenuListFooter;
  Item: typeof MenuItem;
  Trigger: typeof MenuTrigger;
  Divider: typeof MenuDivider;
  SectionTitle: typeof MenuSectionTitle;
  Submenu: typeof Submenu;
  SubmenuTrigger: typeof SubmenuTrigger;
};

export const Menu = OriginalMenu as CompoundMenu;
Menu.List = MenuList;
Menu.ListHeader = MenuListHeader;
Menu.ListFooter = MenuListFooter;
Menu.Item = MenuItem;
Menu.Trigger = MenuTrigger;
Menu.Divider = MenuDivider;
Menu.SectionTitle = MenuSectionTitle;
Menu.Submenu = Submenu;
Menu.SubmenuTrigger = SubmenuTrigger;
