import { cx, css } from 'emotion';
import React from 'react';

import type {
  CommonProps,
  PolymorphicProps,
  PolymorphicComponent,
  ExpandProps,
} from '@contentful/f36-core';

const LIST_DEFAULT_TAG = 'ul';

export interface ListInternalProps extends CommonProps {
  as?: 'ul' | 'ol';
  children?: React.ReactNode;
}

export type ListProps<
  E extends React.ElementType = typeof LIST_DEFAULT_TAG
> = PolymorphicProps<ListInternalProps, E>;

/**
 * List is component that helps with vertical indexing of content.
 * Every list item begins with a bullet or a number.
 */
function _List<E extends React.ElementType = typeof LIST_DEFAULT_TAG>(
  {
    as,
    className,
    children,
    testId = 'cf-ui-list',
    ...otherProps
  }: ListProps<E>,
  ref: React.Ref<any>,
) {
  const Element: React.ElementType = as || LIST_DEFAULT_TAG;

  return (
    <Element
      data-test-id={testId}
      className={cx(
        css({
          margin: 0,
        }),
        className,
      )}
      ref={ref}
      {...otherProps}
    >
      {children}
    </Element>
  );
}

_List.displayName = 'List';

export const List: PolymorphicComponent<
  ExpandProps<ListInternalProps>,
  typeof LIST_DEFAULT_TAG
> = React.forwardRef(_List);
import { cx, css } from 'emotion';
import React from 'react';
import tokens from '@contentful/f36-tokens';
import type { CommonProps, PropsWithHTMLElement } from '@contentful/f36-core';

import { List } from '../List';

export type ListItemProps = PropsWithHTMLElement<
  CommonProps & {
    children?: React.ReactNode;
  },
  'li'
>;

export const ListItem = ({
  className,
  children,
  testId = 'cf-ui-list-item',
  ...otherProps
}: ListItemProps): React.ReactElement => {
  return (
    <li
      className={cx(
        css({
          fontSize: tokens.fontSizeM,
          fontFamily: tokens.fontStackPrimary,
          lineHeight: tokens.lineHeightL,
          // eslint-disable-next-line @typescript-eslint/no-explicit-any
          listStyle: (children as any).type === List ? 'none' : '',
        }),
        className,
      )}
      data-test-id={testId}
      {...otherProps}
    >
      {children}
    </li>
  );
};

ListItem.displayName = 'ListItem';
import { List as OriginalList } from './List';
import { ListItem } from './ListItem/ListItem';

type CompoundList = typeof OriginalList & {
  Item: typeof ListItem;
};

export const List = OriginalList as CompoundList;
List.Item = ListItem;
