import { css } from 'emotion';
import tokens from '@contentful/f36-tokens';

const tooltipMargin = `calc(-1 * ((1rem * (10 / ${tokens.fontBaseDefault})) / 2))`;
const tooltipChevronSize = `calc(1rem * (10 / ${tokens.fontBaseDefault}))`;

export function getStyles() {
  return {
    tooltipTargetWrapper: css({
      display: 'inline-block',
      position: 'relative',
    }),
    tooltip: css({
      background: tokens.gray900,
      fontFamily: tokens.fontStackPrimary,
      fontSize: tokens.fontSizeS,
      fontWeight: tokens.fontWeightNormal,
      fontStyle: 'normal',
      textDecoration: 'none',
      color: tokens.colorWhite,
      textAlign: 'center',
      lineHeight: tokens.lineHeightM,
      padding: `${tokens.spacingXs} calc(1rem * (10 / ${tokens.fontBaseDefault}))`,
      borderRadius: tokens.borderRadiusMedium,
      whiteSpace: 'normal',
    }),
    tooltipHidden: css({
      visibility: 'hidden',
      pointerEvents: 'none',
      zIndex: tokens.zIndexNegative,
    }),
    tooltipContainer: css({
      '& > button[disabled]': { pointerEvents: 'none' },
    }),
    tooltipArrow: css({
      position: 'absolute',
      '&[data-placement*="top"]': {
        bottom: 0,
        left: 0,
        marginBottom: tooltipMargin,
      },
      '&[data-placement*="right"]': {
        left: 0,
        marginLeft: tooltipMargin,
      },
      '&[data-placement*="bottom"]': {
        left: 0,
        top: 0,
        marginTop: tooltipMargin,
      },
      '&[data-placement*="left"]': {
        right: 0,
        marginRight: tooltipMargin,
      },
      '&::before': {
        content: '""',
        display: 'block',
        height: tooltipChevronSize,
        width: tooltipChevronSize,
        backgroundColor: tokens.gray900,
        transform: 'rotate3d(0, 0, 1, 45deg)',
        zIndex: tokens.zIndexNegative,
      },
    }),
  };
}
import React, {
  useEffect,
  useState,
  useRef,
  MouseEvent,
  FocusEvent,
  CSSProperties,
} from 'react';
import { usePopper } from 'react-popper';
import type { Placement } from '@popperjs/core';
import { cx } from 'emotion';
import type * as CSS from 'csstype';

import { getStyles } from './Tooltip.styles';

import tokens from '@contentful/f36-tokens';

import { Portal } from '@contentful/f36-utils';
import type { CommonProps } from '@contentful/f36-core';
import { Box, useId } from '@contentful/f36-core';

export type TooltipPlacement = Placement;

export interface TooltipProps extends CommonProps {
  /**
   * Child nodes to be rendered in the component and that will show the tooltip when they are hovered
   */
  children: React.ReactNode;
  /**
   * HTML element used to wrap the target of the tooltip
   */
  as?: React.ElementType;
  /**
   * Content of the tooltip
   */
  content?: string;
  /**
   * A unique id of the tooltip
   */
  id?: string;
  /**
   * It controls the initial visibility of the tooltip
   */
  isVisible?: boolean;
  /**
   * It sets a max-width for the tooltip
   */
  maxWidth?: number | CSS.Property.MaxWidth;
  /**
   * It sets a delay period for the tooltip
   */
  hideDelay?: number;
  /**
   * Function that will be called when target gets blurred
   */
  onBlur?: (evt: FocusEvent) => void;
  /**
   * Function that will be called when target gets focused
   */
  onFocus?: (evt: FocusEvent) => void;
  /**
   * Function that will be called when the user move the mouse out of the target
   */
  onMouseLeave?: (evt: MouseEvent) => void;
  /**
   * Function that will be called when the user move the mouse over of the target
   */
  onMouseOver?: (evt: MouseEvent) => void;
  /**
   * Function that will be called when the user uses a keyboard key on the target
   */
  onKeyDown?: (evt: KeyboardEvent) => void;

  /**
   * It sets the "preferred" position of the tooltip
   */
  placement?: TooltipPlacement;
  /**
   * Class names to be appended to the className prop of the tooltipâ€™s target
   */
  targetWrapperClassName?: string;
  /**
   * Boolean to control whether or not to render the tooltip in a React Portal.
   * Rendering content inside a Portal allows the tooltip to escape the bounds
   * of its parent while still being positioned correctly. Using a Portal is
   * necessary if an ancestor of the tooltip hides overflow.
   *
   * Defaults to `false`
   */
  usePortal?: boolean;
  /**
   * Prevents showing the tooltip
   * @default false
   */
  isDisabled?: boolean;
}

export const Tooltip = ({
  children,
  className,
  as: HtmlTag = 'span',
  content,
  id,
  isVisible,
  hideDelay = 0,
  onBlur,
  onFocus,
  onMouseLeave,
  onMouseOver,
  onKeyDown,
  targetWrapperClassName,
  maxWidth = 360,
  testId = 'cf-ui-tooltip',
  placement = 'auto',
  usePortal = false,
  isDisabled = false,
  ...otherProps
}: TooltipProps) => {
  const styles = getStyles();
  const [show, setShow] = useState(false);
  const tooltipId = useId(id, 'tooltip');
  const elementRef = useRef(null);
  const popperRef = useRef(null);
  const [arrowRef, setArrowRef] = useState<HTMLSpanElement | null>(null);
  const { styles: popperStyles, attributes, forceUpdate } = usePopper(
    elementRef.current,
    popperRef.current,
    {
      placement: placement,
      modifiers: [
        {
          name: 'arrow',
          options: {
            element: arrowRef,
            padding: parseFloat(tokens.borderRadiusSmall),
          },
        },
        {
          name: 'offset',
          options: {
            offset: [0, 10],
          },
        },
      ],
    },
  );

  // necessary to update tooltip position in case the content is being updated
  useEffect(() => {
    if (forceUpdate !== null) {
      forceUpdate();
    }
  }, [content, forceUpdate]);

  const [isHoveringTarget, setIsHoveringTarget] = useState(false);
  const [isHoveringContent, setIsHoveringContent] = useState(false);
  useEffect(() => {
    setShow(isHoveringContent || isHoveringTarget);
  }, [isHoveringTarget, isHoveringContent]);

  useEffect(() => {
    if (isVisible) setShow(true);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const contentMaxWidth =
    typeof maxWidth === 'string' ? maxWidth : `${maxWidth}px`;

  const contentStyles: CSSProperties = {
    zIndex: tokens.zIndexTooltip,
    maxWidth: contentMaxWidth,
    ...popperStyles.popper,
  };

  if (!content || isDisabled) {
    return (
      <Box as={HtmlTag} className={targetWrapperClassName}>
        {children}
      </Box>
    );
  }

  const tooltip = (
    <Box
      as="span"
      id={tooltipId}
      ref={popperRef}
      role="tooltip"
      style={contentStyles}
      className={cx(styles.tooltip, className)}
      testId={testId}
      onMouseEnter={() => {
        setIsHoveringContent(true);
      }}
      onMouseLeave={() => {
        setIsHoveringContent(false);
      }}
      {...attributes.popper}
    >
      <span>{content}</span>
      <span
        className={styles.tooltipArrow}
        data-placement={
          attributes.popper && attributes.popper['data-popper-placement']
        }
        ref={setArrowRef}
        style={popperStyles.arrow}
      />
    </Box>
  );

  return (
    <>
      {show ? <>{usePortal ? <Portal>{tooltip}</Portal> : tooltip}</> : null}
      <Box
        as={HtmlTag}
        ref={elementRef}
        className={cx(styles.tooltipContainer, targetWrapperClassName)}
        onMouseEnter={(evt: MouseEvent) => {
          setIsHoveringTarget(true);
          if (onMouseOver) onMouseOver(evt);
        }}
        onMouseLeave={(evt: MouseEvent) => {
          setTimeout(() => setIsHoveringTarget(false), hideDelay);
          if (onMouseLeave) onMouseLeave(evt);
        }}
        onFocus={(evt: FocusEvent) => {
          setIsHoveringTarget(true);
          if (onFocus) onFocus(evt);
        }}
        onBlur={(evt: FocusEvent) => {
          setTimeout(() => setIsHoveringTarget(false), hideDelay);
          if (onBlur) onBlur(evt);
        }}
        onKeyDown={(evt: KeyboardEvent) => {
          if (evt.key === 'Escape') {
            setTimeout(() => setIsHoveringTarget(false), hideDelay);
          }
          if (onKeyDown) onKeyDown(evt);
        }}
        {...otherProps}
      >
        {React.Children.map<React.ReactNode, React.ReactNode>(
          children,
          (child) => {
            if (React.isValidElement(child)) {
              return React.cloneElement(child, {
                'aria-describedby': tooltipId,
              });
            }
          },
        )}
      </Box>
    </>
  );
};
