import utcPlugin from 'dayjs/plugin/utc';
import relativeTime from 'dayjs/plugin/relativeTime';
import calendarPlugin from 'dayjs/plugin/calendar';
dayjs.extend(utcPlugin);
dayjs.extend(relativeTime);
