import { useRef, useState } from 'react';

/**
 * An asyncronous state hook.
 *
 * Think of it as a slight modification of React's `useState` hook, where the
 * state returned is instead a `MutableRefObject` like you know it from the
 * `useRef` hook.
 *
 * @param value
 * @returns A tuple with the `MutableRefObject` and a function to set the state
 */
export function useAsyncState<ValueType>(
  value: ValueType,
): [React.MutableRefObject<ValueType>, (state: ValueType) => void] {
  const ref = useRef<ValueType>(value);
  const [, forceUpdate] = useState(false);

  const setState = (newState: ValueType) => {
    if (!Object.is(ref.current, newState)) {
      ref.current = newState;
      forceUpdate((state) => !state);
    }
  };

  return [ref, setState];
}
import { css } from 'emotion';
import type { CSSObject } from '@emotion/serialize';
import tokens from '@contentful/f36-tokens';
import { NotificationVariant } from '../types';

const variantColors = {
  positive: tokens.green600,
  negative: tokens.red600,
  warning: tokens.orange400,
};

const getWrapperStyle = ({ variant }) =>
  css({
    background: tokens.colorWhite,
    borderRadius: tokens.borderRadiusMedium,
    boxShadow: tokens.boxShadowHeavy,
    boxSizing: 'border-box',
    display: 'flex',
    overflow: 'hidden',
    position: 'relative',
    padding: tokens.spacingM,
    width: '100%',
    '&:before': {
      backgroundColor: variantColors[variant],
      content: '""',
      height: '100%',
      left: 0,
      position: 'absolute',
      top: 0,
      width: '2px',
    },
  });

const getIconStyle = ({ variant }) =>
  css({
    fill: variantColors[variant],
    marginRight: tokens.spacingM,
    alignItems: 'flex-start',
  });

const notificationStyle: CSSObject = {
  fontSize: tokens.fontSizeM,
  lineHeight: tokens.lineHeightM,
  width: '100%',
};

const titleStyle: CSSObject = {
  color: tokens.gray800,
  fontSize: tokens.fontSizeL,
  lineHeight: tokens.lineHeightL,
};

const contentStyle: CSSObject = {
  color: tokens.gray700,
  '&:last-child': {
    marginBottom: 0,
  },
};

const closeButtonStyle: CSSObject = {
  marginBottom: `-${tokens.spacingXs}`,
  marginRight: `-${tokens.spacingXs}`,
  marginTop: `-${tokens.spacingXs}`,
};

export const getStyles = ({ variant }: { variant: NotificationVariant }) => ({
  wrapper: getWrapperStyle({ variant }),
  icon: getIconStyle({ variant }),
  notification: css(notificationStyle),
  title: css(titleStyle),
  content: css(contentStyle),
  closeButton: css(closeButtonStyle),
});
import React from 'react';
import { cx } from 'emotion';
import {
  CheckCircleIcon,
  ErrorCircleIcon,
  WarningIcon,
  CloseIcon,
} from '@contentful/f36-icons';

import { Button } from '@contentful/f36-button';
import { TextLink } from '@contentful/f36-text-link';
import { Flex, Box } from '@contentful/f36-core';
import type { ExpandProps, CommonProps } from '@contentful/f36-core';
import { Heading, Paragraph } from '@contentful/f36-typography';

import { NotificationVariant, NotificationCta } from '../types';
import { getStyles } from './NotificationItem.styles';

export interface NotificationItemProps extends CommonProps {
  /**
   * Defines the styling of notification
   * @default positive
   */
  variant?: NotificationVariant;
  /**
   * Defines if the close button should be rendered
   * @default true
   */
  withCloseButton?: boolean;
  /**
   * Function that will be triggered when close button is clicked
   */
  onClose?: Function;
  /**
   * Title of the notification
   */
  title?: string;
  /**
   * Content of the notificaiton
   */
  children: React.ReactNode;
  /**
   * Label and text-link props of the CTA
   */
  cta?: Partial<NotificationCta>;
}

const _NotificationItem = (props: ExpandProps<NotificationItemProps>, ref) => {
  const {
    className,
    children,
    cta,
    withCloseButton = true,
    variant = 'positive',
    onClose,
    testId = 'cf-ui-notification',
    title,
    ...otherProps
  } = props;

  const styles = getStyles({ variant });

  const iconSize = title ? 'medium' : 'small';
  const iconVariants = {
    positive: <CheckCircleIcon variant={variant} size={iconSize} />,
    warning: <WarningIcon variant={variant} size={iconSize} />,
    negative: <ErrorCircleIcon variant={variant} size={iconSize} />,
  };

  const intents = {
    positive: 'success',
    warning: 'warning',
    negative: 'error',
  };

  return (
    <Flex
      data-test-id={testId}
      role="alert"
      data-intent={intents[variant]}
      aria-live={variant === 'positive' ? 'polite' : 'assertive'}
      className={cx(styles.wrapper, className)}
      {...otherProps}
      ref={ref}
    >
      <Box className={cx(styles.icon)}>{iconVariants[variant]}</Box>
      <Box className={cx(styles.notification)}>
        {title && (
          <Heading
            as="h2"
            className={cx(styles.title)}
            marginBottom="spacingXs"
          >
            {title}
          </Heading>
        )}
        {children && (
          <Paragraph className={styles.content} marginBottom="spacingXs">
            {children}
          </Paragraph>
        )}
        {cta?.label && (
          <TextLink
            {...cta?.textLinkProps}
            as={cta?.textLinkProps?.as || 'button'}
          >
            {cta.label}
          </TextLink>
        )}
      </Box>
      {withCloseButton && (
        <Box>
          <Button
            className={cx(styles.closeButton)}
            variant="transparent"
            startIcon={<CloseIcon />}
