import React, { useEffect } from 'react';

export interface TypographyProps {
  children?: React.ReactNode;
}

/**
 * @deprecated
 */
export const Typography = (props: TypographyProps) => {
  useEffect(() => {
    console.warn(
      'Forma 36: Typography component is deprecated. You can safely remove it from your components.',
    );
  }, []);
  return <>{props.children}</>;
};

Typography.displayName = 'Typography';
import React from 'react';
import { FontSizeTokens, LineHeightTokens } from '@contentful/f36-tokens';
import type {
  CommonProps,
  MarginProps,
  PolymorphicComponent,
  PolymorphicProps,
  ExpandProps,
} from '@contentful/f36-core';
import { Text } from '../Text';
import type { HeadingElement } from '../Heading';

const DISPLAY_TEXT_DEFAULT_TAG = 'h2';

export interface DisplayTextInternalProps extends CommonProps, MarginProps {
  size?: 'default' | 'large';
  as?: HeadingElement;
  isTruncated?: boolean;
  isWordBreak?: boolean;
}

export type DisplayTextProps<
  E extends React.ElementType = typeof DISPLAY_TEXT_DEFAULT_TAG
> = PolymorphicProps<DisplayTextInternalProps, E>;

function _DisplayText<
  E extends React.ElementType = typeof DISPLAY_TEXT_DEFAULT_TAG
>(
  {
    children,
    size = 'default',
    testId = 'cf-ui-display-text',
    ...otherProps
  }: DisplayTextProps<E>,
  ref: React.Ref<any>,
) {
  let fontSize: FontSizeTokens = 'fontSize2Xl';
  let lineHeight: LineHeightTokens = 'lineHeight2Xl';

  if (size === 'large') {
    fontSize = 'fontSize3Xl';
    lineHeight = 'lineHeight3Xl';
  }

  return (
    <Text
      as={DISPLAY_TEXT_DEFAULT_TAG}
      testId={testId}
      marginBottom={size === 'default' ? 'spacingL' : 'spacingXl'}
      fontSize={fontSize}
      lineHeight={lineHeight}
      fontColor="gray900"
      fontWeight="fontWeightDemiBold"
      {...otherProps}
      ref={ref}
    >
      {children}
    </Text>
  );
}

_DisplayText.displayName = 'DisplayText';

export const DisplayText: PolymorphicComponent<
  ExpandProps<DisplayTextInternalProps>,
  typeof DISPLAY_TEXT_DEFAULT_TAG
> = React.forwardRef(_DisplayText);
import React from 'react';
import tokens from '@contentful/f36-tokens';
import { css, cx } from 'emotion';
import type {
  CommonProps,
  MarginProps,
  PolymorphicComponent,
  PolymorphicProps,
  ExpandProps,
} from '@contentful/f36-core';
import type { HeadingElement } from '../Heading';
import { Text } from '../Text';

const SECTION_HEADING_DEFAULT_TAG = 'h2';

export interface SectionHeadingInternalProps extends CommonProps, MarginProps {
  as?: HeadingElement;
  isTruncated?: boolean;
  isWordBreak?: boolean;
}

export type SectionHeadingProps<
  E extends React.ElementType = typeof SECTION_HEADING_DEFAULT_TAG
> = PolymorphicProps<SectionHeadingInternalProps, E>;

function _SectionHeading<
  E extends React.ElementType = typeof SECTION_HEADING_DEFAULT_TAG
>(
  {
    children,
    className,
    testId = 'cf-ui-section-heading',
    ...otherProps
  }: SectionHeadingProps<E>,
  ref: React.Ref<any>,
) {
  return (
    <Text
      as={SECTION_HEADING_DEFAULT_TAG}
      testId={testId}
      marginBottom="spacingL"
      fontWeight="fontWeightDemiBold"
      fontColor="gray900"
      fontSize="fontSizeS"
      lineHeight="lineHeightS"
      className={cx(
        css({
          letterSpacing: tokens.letterSpacingWide,
          textTransform: 'uppercase',
        }),
        className,
      )}
      {...otherProps}
      ref={ref}
    >
      {children}
    </Text>
  );
}

_SectionHeading.displayName = 'SectionHeading';

export const SectionHeading: PolymorphicComponent<
  ExpandProps<SectionHeadingInternalProps>,
  typeof SECTION_HEADING_DEFAULT_TAG
> = React.forwardRef(_SectionHeading);
import React from 'react';
import type {
  PropsWithHTMLElement,
  CommonProps,
  MarginProps,
  ExpandProps,
} from '@contentful/f36-core';
import { Text } from '../Text';

export type ParagraphInternalProps = CommonProps &
  MarginProps & {
    children: React.ReactNode;
    isTruncated?: boolean;
    isWordBreak?: boolean;
  };

export type ParagraphProps = PropsWithHTMLElement<ParagraphInternalProps, 'p'>;

export const Paragraph = React.forwardRef<
  HTMLParagraphElement,
  ExpandProps<ParagraphProps>
>(({ children, testId = 'cf-ui-paragraph', ...otherProps }, ref) => {
  return (
    <Text
      as="p"
      testId={testId}
      marginBottom="spacingM"
      lineHeight="lineHeightM"
      {...otherProps}
      ref={ref}
    >
      {children}
    </Text>
  );
});

Paragraph.displayName = 'Paragraph';
